#!/bin/bash
set -e; ACTION="${1%%,*}"; RED='\033[0;31m'; GREEN='\033[0;32m'; NC='\033[0m'
log_info() { printf "${GREEN}✓${NC} %s\n" "$1"; }; log_error() { printf "${RED}✗${NC} %s\n" "$1"; exit 1; }
detect_os() { source /etc/os-release || log_error "Cannot detect OS"; case "${ID,,}" in ubuntu|debian|raspbian|linuxmint|pop) PKG_UPDATE="apt-get update"; PKG_INSTALL="apt-get install -y"; PKG_UNINSTALL="apt-get remove -y"; PKG="bind9-utils" ;; fedora|rhel|centos|rocky|alma) PKG_UPDATE="dnf check-update || true"; PKG_INSTALL="dnf install -y"; PKG_UNINSTALL="dnf remove -y"; PKG="bind-utils" ;; arch|manjaro|endeavouros) PKG_UPDATE="pacman -Sy"; PKG_INSTALL="pacman -S --noconfirm"; PKG_UNINSTALL="pacman -R --noconfirm"; PKG="bind-tools" ;; opensuse*|sles) PKG_UPDATE="zypper refresh"; PKG_INSTALL="zypper install -y"; PKG_UNINSTALL="zypper remove -y"; PKG="bind-utils" ;; alpine) PKG_UPDATE="apk update"; PKG_INSTALL="apk add"; PKG_UNINSTALL="apk del"; PKG="bind-tools" ;; *) log_error "Unsupported distribution" ;; esac; }
case "$ACTION" in install) log_info "Installing bind-utils..."; detect_os; sudo $PKG_UPDATE || true; sudo $PKG_INSTALL $PKG || log_error "Failed"; log_info "bind-utils installed!" ;; update) log_info "Updating bind-utils..."; detect_os; sudo $PKG_UPDATE || true; sudo $PKG_INSTALL $PKG || log_error "Failed"; log_info "bind-utils updated!" ;; uninstall) log_info "Uninstalling bind-utils..."; detect_os; sudo $PKG_UNINSTALL $PKG || log_error "Failed"; log_info "bind-utils uninstalled!" ;; config) log_info "bind-utils includes: dig, nslookup, host"; dig --version | head -1 ;; *) log_error "Unknown action: $ACTION" ;; esac
