#!/bin/bash
set -e; ACTION="${1%%,*}"; RED='\033[0;31m'; GREEN='\033[0;32m'; NC='\033[0m'
log_info() { printf "${GREEN}✓${NC} %s\n" "$1"; }; log_error() { printf "${RED}✗${NC} %s\n" "$1"; exit 1; }
detect_os() { source /etc/os-release || log_error "Cannot detect OS"; case "${ID,,}" in ubuntu|debian|raspbian|linuxmint|pop) PKG_UPDATE="apt-get update"; PKG_INSTALL="apt-get install -y"; PKG_UNINSTALL="apt-get remove -y"; PKG="build-essential" ;; fedora|rhel|centos|rocky|alma) PKG_UPDATE="dnf check-update || true"; PKG_INSTALL="dnf groupinstall -y"; PKG_UNINSTALL="dnf groupremove -y"; PKG="'Development Tools'" ;; arch|manjaro|endeavouros) PKG_UPDATE="pacman -Sy"; PKG_INSTALL="pacman -S --noconfirm"; PKG_UNINSTALL="pacman -R --noconfirm"; PKG="base-devel" ;; opensuse*|sles) PKG_UPDATE="zypper refresh"; PKG_INSTALL="zypper install -y -t pattern"; PKG_UNINSTALL="zypper remove -y -t pattern"; PKG="devel_basis" ;; alpine) PKG_UPDATE="apk update"; PKG_INSTALL="apk add"; PKG_UNINSTALL="apk del"; PKG="build-base" ;; *) log_error "Unsupported distribution" ;; esac; }
case "$ACTION" in install) log_info "Installing build-essential..."; detect_os; sudo $PKG_UPDATE || true; sudo $PKG_INSTALL $PKG || log_error "Failed"; log_info "build-essential installed!" ;; update) log_info "Updating build-essential..."; detect_os; sudo $PKG_UPDATE || true; sudo $PKG_INSTALL $PKG || log_error "Failed"; log_info "build-essential updated!" ;; uninstall) log_info "Uninstalling build-essential..."; detect_os; sudo $PKG_UNINSTALL $PKG || log_error "Failed"; log_info "build-essential uninstalled!" ;; config) log_info "build-essential includes: gcc, g++, make, gdb"; gcc --version | head -1 ;; *) log_error "Unknown action: $ACTION" ;; esac
