#!/bin/bash
set -e; ACTION="${1%%,*}"; PARAMS_REST="${1#*,}"; [[ -n "$PARAMS_REST" && "$PARAMS_REST" != "$1" ]] && while IFS='=' read -r key val; do [[ -n "$key" ]] && export "$key=$val"; done <<< "${PARAMS_REST//,/$'\n'}"
RED='\033[0;31m'; GREEN='\033[0;32m'; NC='\033[0m'
log_info() { printf "${GREEN}✓${NC} %s\n" "$1"; }; log_error() { printf "${RED}✗${NC} %s\n" "$1"; exit 1; }
detect_os() { source /etc/os-release || log_error "Cannot detect OS"; case "${ID,,}" in ubuntu|debian|raspbian|linuxmint|pop) PKG_UPDATE="apt-get update"; PKG_INSTALL="apt-get install -y"; PKG_UNINSTALL="apt-get remove -y"; PKG="wireguard wireguard-tools" ;; fedora|rhel|centos|rocky|alma) PKG_UPDATE="dnf check-update || true"; PKG_INSTALL="dnf install -y"; PKG_UNINSTALL="dnf remove -y"; PKG="wireguard-tools" ;; arch|manjaro|endeavouros) PKG_UPDATE="pacman -Sy"; PKG_INSTALL="pacman -S --noconfirm"; PKG_UNINSTALL="pacman -R --noconfirm"; PKG="wireguard-tools" ;; opensuse*|sles) PKG_UPDATE="zypper refresh"; PKG_INSTALL="zypper install -y"; PKG_UNINSTALL="zypper remove -y"; PKG="wireguard-tools" ;; alpine) PKG_UPDATE="apk update"; PKG_INSTALL="apk add"; PKG_UNINSTALL="apk del"; PKG="wireguard-tools" ;; *) log_error "Unsupported distribution" ;; esac; }
case "$ACTION" in install) log_info "Installing wireguard..."; detect_os; sudo $PKG_UPDATE || true; sudo $PKG_INSTALL $PKG || log_error "Failed"; log_info "wireguard installed!" ;; update) log_info "Updating wireguard..."; detect_os; sudo $PKG_UPDATE || true; sudo $PKG_INSTALL $PKG || log_error "Failed"; log_info "wireguard updated!" ;; uninstall) log_info "Uninstalling wireguard..."; detect_os; sudo $PKG_UNINSTALL $PKG || log_error "Failed"; log_info "wireguard uninstalled!" ;; config) log_info "wireguard configuration"; log_info "Generate keys: wg genkey | tee privatekey | wg pubkey > publickey"; log_info "Create /etc/wireguard/wg0.conf and bring up: sudo wg-quick up wg0" ;; *) log_error "Unknown action: $ACTION" ;; esac
